import React, { useState, useEffect } from "react";

const App = () => {

    const [city, setCity] = useState(null);
    const [search, setSearch] = useState("Mumbai");

    useEffect(() => {
        const fetchApi = async () => {
            const url = `https://api.openweathermap.org/data/2.5/weather?q=${search}&appid=4fa3d2d1184167f592ceee92ae5164f1`;
            const res = await fetch(url);
            const resjson = await res.json();
            // console.log(resjson);
            setCity(resJson);
        };

        fetchApi();
    })


    return (
        <>
            <div className="box">
                <div className="inputData">
                    <input type="search" className="inputField" onChange={(e) => {

                    }} />
                </div>

                <div className="info">
                    <h2 className="location">
                        <i className="fas fa-street-view"></i>{search}
                    </h2>
                    <h1 className="temp">

                    </h1>
                    <h3 className="tempmin_max">Min : 5.25°Cel | Max : 5.25°Cel</h3>
                </div>
                <div className="wave -one"></div>
                <div className="wave -two"></div>
                <div className="wave -three"></div>
            </div>
        </>
    );
};

export default App;
